document.addEventListener('DOMContentLoaded',()=>{const $navbarBurgers=Array.prototype.slice.call(document.querySelectorAll('.navbar-burger'),0);if($navbarBurgers.length>0){$navbarBurgers.forEach(el=>{el.addEventListener('click',()=>{const target=el.dataset.target;const $target=document.getElementById(target);el.classList.toggle('is-active');$target.classList.toggle('is-active');});});}});function imageshow(){document.getElementById('imagemodal').style.display='flex';}
function imagehide(){document.getElementById('imagemodal').style.display='none';}
document.onreadystatechange=function(){if(document.readyState=="complete"){var giftofspeed=document.createElement('link');giftofspeed.rel='stylesheet';giftofspeed.href='http://localhost:8000/theme/css/font-awesome.min.css?v=0';giftofspeed.type='text/css';var godefer=document.getElementsByTagName('link')[0];godefer.parentNode.insertBefore(giftofspeed,godefer);try{let rot13=document.getElementById('email').href.replace(/[a-zA-Z]/g,function(c){return String.fromCharCode((c<="Z"?90:122)>=(c=c.charCodeAt(0)+13)?c:c-26);});document.getElementById('email').href=rot13;let subject=document.getElementsByClassName('article-title')[0].innerHTML;subject=subject.substr(subject.search('&nbsp')+6);rot13+='?subject='+subject;document.getElementById('feedback').innerHTML='<a href="'+rot13+'"><i class="fas fa-at"></i>&nbsp;Send feedback</a>';}catch(_){}}};